let localquote=[
{"text": "قد نضيق بالحبّ إذا وُجد، ولكن شَدَّ مانفتقده إذا ذهب.", "author": "نجيب محفوظ"},
{"text": "أريدها لا تعرفني ولا أعرفها، لا من شيءٍ إلا لأنها تعرفني وأعرفها.. تتكلم ساكتةً وأرد عليها بسكوتي. صمتٌ ضائعٌ كالعبث ولكن له في القلبين عمل كلامٍ طويل", "author": "مصطفى صادق الرافعي"},
{"text": "قاعدة أخري", "author": "Elif Shafak"},
{"text": "ليس الجود أن تعطيني ما أنا أشدّ منك حاجة إليه ، وإنما الجود أن تعطيني ما أنت أشدّ إليه حاجة مني", "author": "جبران خليل جبران"},
{"text": "إذا ميز الرجل المرأة بين جميع النساء؛ فذلك هو الحب.", "author": "عباس محمود العقاد"},
{"text": "ما طلبتُ من الله في ليلة القدر سوى .. أن تكون قدري وستري .. سقفي ، وجدران عمري .. وحلالي ساعة الحشرِ", "author": "أحلام مستغانمي"},
{"text": "عندما التقيا .. لم يكن الزمان صالحا للحب", "author": "محمد المنسي قنديل"},
{"text": "الفرق بين الحب الفاشل والحب الناجح، أن الأول يؤلمك شهرا، في حين الثاني يؤلمك مدى الحياة", "author": "محمد عفيفي"},
{"text": "كم نمنح أنفسنا من الطمأنينة والراحة والسعادة، حين نمنح الآخرين عطفنا وحبنا وثقتنا، يوم تنمو في نفوسنا بذرة الحب والعطف والخير", "author": "سيد قطب"},
{"text": "إذا شئتم أن تذوقوا أجمل لذائذ الدنيا، وأحلى أفراح القلوب، فجودوا بالحب وبالعواطف كما تجودون بالمال", "author": "علي الطنطاوي"},
{"text": "لا؛ لا يصح أن يبنى الزواج على الحب وحده إلا إن صح أن تبنى العمارة الضخمة على أساس من الملح، في مجرى الماء", "author": "علي الطنطاوي"},
{"text": "صدقني يا عزيز لقد تسببت لي بكل أسباب البكاء", "author": "أثير عبدالله النشمي"},
{"text": "من قال بأن الحب يمنحنا الحياة؟!", "author": "أثير عبدالله النشمي"},
{"text": "كان فرق السن بينى و بينها يزيد على خمسة عشر سنة . أتظن أني شعرت بذلك أو أنها شعرت به ؟ الحب يا ولدي إلتقاء روحين", "author": "بهاء طاهر"},
{"text": "أعطني أذناً أعطك صوتاً", "author": "جبران خليل جبران"},
{"text": "إنَّ البوح يُحيي الحب والكتمانُ يُميته", "author": "يوسف زيدان"},
{"text": "سَلامٌ للذين أحبُّهم عبثاً", "author": "محمود درويش"},
{"text": "إنك يا سيد مصطفى سعيد رغم تفوقك العلمي رجل غبي، إن في تكوينك الروحي بقعة مظلمة، لقد بددت أعظم طاقة يمنحها الله للناس : طاقة الحب", "author": "الطيب صالح"},
{"text": "صدقني يا عزيز ما النسيان الا ادعاء", "author": "أثير عبدالله النشمي"},
{"text": "الحب سعادة ترتعش", "author": "جبران خليل جبران"},
{"text": "أتعرف بأنك أصبحت تكتب مثلما تتحدث!..أأصبحت تشبهني بعدما انتهي كل شئ؟", "author": "أثير عبدالله النشمي"},
{"text": "كلنا قد تاب يوما", "author": "مظفر النواب"},
{"text": "آه يا حبيبتي ، ألم يكن من الممكن أن نكون أسعد حالا لو لم يكلل حبنا بكل هذا النجاح ؟", "author": "محمد عفيفي"},
{"text": "خلى بالك من نفسك", "author": "احمد مراد_الفيل الازرق"},
{"text": "‏لن تَستطيع أن تُعطي بدون الحُب، ولن تستطيع أن تحِب بدون التسامح", "author": "إبراهيم الفقي"},
{"text": "هناك فرق شاسع بين من يطيعك حباً ومن يطيعك خوفاً", "author": "غازي القصيبي"},
{"text": "الحب شيء والنضال من أجله شيء آخر", "author": "محمد حسن علوان"},
{"text": "أَعطنا، يا حبّ، فَيْضَكَ كلَّه لنخوض", "author": "محمود درويش"},
{"text": "ولما كان خفق الحبّ في قلبي هو النجوى بلا صاحب", "author": "صلاح عبد الصبور"},
{"text": "إن الحب قصة لا يجب أن تنتهى ..إن الحب مسألة رياضية لم تحل ..إن جوهر الحب مثل جوهر الوجود لابد أن يكون فيه ذلك الذى يسمونه \"المجهول\" أو", "author": "توفيق الحكيم"},
{"text": "النساء يحببن أن يجمعن شهادات جميلة مثل هذه أحيانا، تشهد أنهن لم يعبرن الحياة بشكل عادي، بل كن جديرات بأن يتسببن في بعض الحب، و بعض الحزن.", "author": "محمد حسن علوان"},
{"text": "الحب .. يعني انك تخاف من كتر الفرحة !", "author": "داليا رشوان"},
{"text": "وخطر لي أننا في الماضي كنا نعرف رجال السياسة بفضل الشعراء.", "author": "بهاء طاهر"},
{"text": "الشرف في الكلمات والوعد والعمل والحب. لاتكن شريفاً في أمرٍ ما وأقل شرفاً في أمرٍ آخر. كُن شريفاً في كل أمور حياتك", "author": "محمد الرطيان"},
{"text": "فجاءة ! انتهت المكالمة ..قررت ان تنتهي المكالمة فجاءة", "author": "أثير عبدالله النشمي"},
{"text": "لما نسيت الأمة حب القلوب، واشتغلت بحب البطون، رضيت بالدون، وعاشت في هون.", "author": "عائض بن عبد الله القرني"},
{"text": "فالحب هو العملة النادرة في كل الأزمنة، وهو الموجود المفقود دوماً، مهما تغنى الناس به", "author": "يوسف زيدان"},
{"text": "إن المحبة المحدودة تطلب امتلاك المحبوب، أما المحبة غير المتناهية فلا تطلب غير ذاتها.", "author": "جبران خليل جبران"},
{"text": "ليس لديك فكرة عما هو الحب : إنه مرضٌ يجعل الإنسان شريراً", "author": "إيميلي نوثومب"},
{"text": "مأساة الالف عقدة تبجأ بغلطة", "author": "أثير عبدالله النشمي"},
{"text": "فاسكو : مارجريتا ! .. (يتنهد) عيناها كجوزتين ، وحولهما الدموع . حين أفكر فيها أشعر أنني حزين وأنني بخير.", "author": "جورج شحادة"},
{"text": "أجل .. أنا أكره تعرية القلوب", "author": "جيلبرت سينوي"},
{"text": "لا اريد ذاك الحب", "author": "ياسمين ثابت"},
{"text": "الحب بحاجة إلى خيال. و الخيال فسيفساء من الكذبات الصغيرة.", "author": "محمد حسن علوان"},
{"text": "لقد صار لدينا تاريخ مشترك، يمكننا النظر إلى الماض ورواية أحداث الأيام التي عشناها معاً، وحساب الأحزان و الأفراح، هكذا يصنع الحب، دون تسرع، ويوماً بيوم", "author": "إيزابيل الليندي"},
{"text": "لقد صرتِ عذابي ، و كتب علي أن ألجأ مرتين إلى المنفى ، هاربا أو مرغما على الفرار من أقرب الأشياء إلى الرجل و أكثرها تجذرا في صدره ، الوطن و الحبّ", "author": "غسان كنفاني"},
{"text": "ضع اللغات كلها في فم المحب، فإن خفقة واحدة من قلبه ستجعلها كلها بلا تأثير كأنها صمتٌ ناطقٌ.", "author": "مصطفى صادق الرافعي"},
{"text": "وقد حاولتُ وباطلاً حاولت أن أتعلم محبته فلم أتعلم. لأن المحبة هي قوةٌ تبتدع قلوبنا، وقلوبنا لا تقدر أن تبتدعها", "author": "جبران خليل جبران "},
{"text": "أترى يا قلبي أن هذا القمر، تليسكوب يكبر صورة العواطف حين تبث في ضوئه فلا يطلع على الحبيبين أبداً إلا كبر أحدهما في عين الآخر", "author": "مصطفى صادق الرافعي"},
{"text": "مذاق المدن كمذاق الحب دائما يتفتح هناك في الوحدة", "author": "إبراهيم نصر الله"},
{"text": "الحب ليس فقط جذوة عشق مشتعلة من البداية , إنما هو أيضاً ممارسة ؛ نعم ممارسة أفعال الحب تشعل جذوته الخامدة حتى هذه اللحظة .", "author": "دعاء عبد الرحمن"},
{"text": "وقعنا في الحب ولم نعترف", "author": "محمد حسن علوان"},
{"text": "ربِّ ... إجعلهُ ملِكاً على عرشِ قلبِي يَحكمهُ بِعدلٍ سيداً على هامّة روحــي يُحيِيها بِعشقٍ", "author": "ساميه جلابي"},
{"text": "الحب لا بيجي بالتسول ولا بالعافية والتباتة", "author": "أمل محمود"},
{"text": "لا شك أن مشاعر الحب الملتهبه تطفئ نار الهموم .. لكن نار الحب التي قد تكوينا ماذا يطفئها ..!!", "author": "مسعد العرباني"},
{"text": "الحب يجعل البشر أكثر خفة وأناقة وجاذبية", "author": "ميرال الطحاوي"},
{"text": "روح العالم نحن القادرين على أن نحب مايستحق الحب ومايشبه ويسكن فيه", "author": "إبراهيم نصر الله"},
{"text": "قال: هل للحب معنى .. غير لثم .... وعناق", "author": "أحمد قنديل"},
{"text": "لا توجد قواعد للحب .. ولكن يوجد قواعد في الحب .. أهمّها الإخلاص !", "author": "جلال الخوالدة"},
{"text": "هــوَ الحــب، كــالمــوج", "author": "محمود درويش"},
{"text": "بل ماذا يبلغ الكلام من حب يجعل الحياة كأنها كلمة رضى في شفتي الحبيبة، ويجعل الحبيبة نفسها كأنها كلمة رضى في شفتي الحياة؟", "author": "مصطفى صادق الرافعي"},
{"text": "كنت ادعوه بجوارحي بكل ما في أن اعرف", "author": "أحمد الفناجيلي"},
{"text": "الصداقة من طرف واحد , اقنعونا بأن اسوأ المشاعر هي تجاهل من احببت لك و تصبح علاقتك به حب من طرف واحد , و لكن لم يخبرونا عن الصداقة من طرف واحد", "author": "عمر مجدي"},
{"text": "الحب الذي لا نعترف به.. يُغوينا !", "author": "ريهام أحمد"},
{"text": "الوفاء‬ فى نظرى هو لون من الوان التماسك الروحى وذلك هو السبب فى عده من مكارم الأخلاق", "author": "زكي مبارك"},
{"text": "وكَمْ ذَا بقلْبي مِنَ الحُبِّ فيكَ", "author": "راقية جلال الدويك"},
{"text": "وقد يكون حبّنا هروبا.. قد يكون هروبا من المذاكرة.. أو من وطأة الحياة اليوميّة.. أو من مسؤوليّات البيت المرهقة.. أو هروبا من أنفسنا..", "author": "مصطفى محمود"},
{"text": "أريد أن يفيض الحب من قلبي فينبع ويثمر", "author": "الطيب صالح"},
{"text": "مُطقأةٌ مَدافِئَ الحُب وبردُ الرحِيلِ قارِسٌ !", "author": "ساميه جلابي"},
{"text": "أن تحب شخصًا يعنى أن تعرف أبشع جوانبه ..ثم تظل راغبًا في أن تحتضنه بين يديك ..فتحتفظ به بين أضلعك ..مخفيا عيوبه عن أعين الناس إلى الأبد ..", "author": "غير معروف"},
{"text": "انت امها، ولدت برحمك و تربت بحجرك كيف تسمعين و تصدقين كلام الاخرين عليها!", "author": "علي الماجد"},
{"text": "- صارحني عمى .. هل تؤمن حقًا ان \"محمود\" سيكتفي بى ؟ أنه ..", "author": "كاردينيا"},
{"text": "ولا خير فيمن لا يحب بلاده...ولا في حليف الحب أن لم يتيم", "author": "مصطفى صادق الرافعي"},
{"text": "الرجل أحياناً يشعر بالذنب والغضب لو أحبته المرأة بإفراط.", "author": "مارلين مونرو"},
{"text": "وإني قادر على أن أضحي بحياتي من أجلها ، ولكن البقاء إلى جانبها يُضجرني!", "author": "ميخائيل ليرمنتوف"},
{"text": "هناك أشخاص لا يقعون في الحب ببساطة ..و أنا منهم ..لا أستطيع أن أسلم مشاعري لأى شخص كان ..أنا ضد العلاقات العابرة أو الافتتان المؤقت..", "author": "غير معروف"},
{"text": "الحب هو تلك العاطفة التى جاورت الرغبة , هو نبض السعادة و هرمون الرضا..", "author": "مروة جمال"},
{"text": "كل الناس غرباء بمجرد فقدهم للحب", "author": "علي الماجد"},
{"text": "ومن لم يؤدبه الحب يؤدبه المزيد من الحب", "author": "نجيب محفوظ العائش في الحقيقة"},
{"text": "الفرح صورة تتكسر في جريان الماء والحب ملوث بخوف الزوال", "author": "أسماء الجلاصي"},
{"text": "انسى كل شيء ،وعش اللحظة", "author": "علي الماجد"},
{"text": "اسوء مافي الحب، انه يفقدك كل وسائل الدفاع، مهما كنت قوياَ.", "author": "علي الماجد"},
{"text": "إن لي نفساً أفسدتها حياة المجتمع الراقي، وخيالاً قلقاً ، وقلباً لا يشبع من جوع، لا شيء يرويني .. فسرعان ما آلف الألم واللذة كليهم", "author": "ميخائيل ليرمنتوف"},
{"text": "ما أكثر الكره وما أقل الحب", "author": "نجيب محفوظ"},
{"text": "ترى، هل يستأذن الحب ليعمر قلبا ما؟", "author": "رسول حمزاتوف"},
{"text": "ان كرامة الحب هي الاستسلام للحب", "author": "أحسان عبد القدوس"},
{"text": "يكاد أن يكون الحب صنم هذا العصر الذي يحرق له البخور ويقدم له الشباب القرابين من دمائهم", "author": "مصطفى محمود"},
{"text": "واعلم أن الزهرة مثل الشهيد تضحي بحياتها لتوصيل رسالة حب.", "author": "جلال عامر"},
{"text": "أسوا ما يعانيه الإنسان هو تبادل الأسرى والقتلى بعد انتهاء الحرب، وتبادل الخطابات والذكريات بعد انتهاء الحب.", "author": "جلال عامر"},
{"text": "واشتقت إليك فعلمني ألا أشتاق فالشوق دليل الحب.", "author": "جلال عامر"},
{"text": "ربما مر للمحب زمان... نال فيه المحب ما قد تمنى", "author": "مصطفى صادق الرافعي"},
{"text": "كل لحظة حب حقيقة هي أمر يحدث خارج الزمن، والإمساك بها يحتاج إلى قفزةٍ خارجه. الحب حياة مضافة غير محسوبة على حياتنا الأساسية.", "author": "حامد الناظر"},
{"text": "الحب الذي لا يمنحنا حيوات كثيرة غنية إنما هو حب راكد، سيصيبه الأسَنُ يومًا ما.", "author": "حامد الناظر"},
{"text": "وليس بالخبز وحده يحيا الإنسان", "author": "نجيب محفوظ"},
{"text": "ينبغى عليك ألا تعطى نفسك الفرصة للانهيار لانك حين تفعلين ذلك يتحول الأمر إلى عادة لديك تتكرر مرارا", "author": "إليزابيث جيلبرت"},
{"text": "لا تعاتب حبيبك الذي مل، فإذا مرض الحب مات العتاب.", "author": "مصطفى صادق الرافعي"},
{"text": "الذليل في رأي الحب من إذا هجرته المرأة كان هجرها إياه عقوبته، والعزيز في رأي الحب من إذا هجرته المرأة كان هجرها إياه عقوبتها.", "author": "مصطفى صادق الرافعي"},
{"text": "خلقت النساء من أجل المعاناة، لا عجب أنهن يطالبن بالحب على الدوام", "author": "تشارلز بوكوفسكي"},
{"text": "إمّا أن يُخلق الحب مع الإنسان وهو في بطن أمه أو لا يُخلق. وكل الحب الذي يأتي بعد ذلك هو نوعٌ من التعويض", "author": "حامد الناظر"},
{"text": "فضلتُ إنتظار الحب الحقيقي الذي عشت بسببه أوهاماً تُثير السخرية", "author": "إيميلي نوثومب"},
{"text": "العقل كان ولا يزال دالاًّ، ولم يكن مُجْبِراً ولا مُلْجِئًا.", "author": "محمد سعيد رمضان البوطي"},
{"text": "موافقتها للزواج منك بالرغم من نفورها منه هو طريقة تستنجد فيها أن تساعدها", "author": "منى الليلي( ام حمدة )"},
{"text": "لن تسعد إلا إذا كنت عاشقاً.. لا شيء يجلب الفرح والسعادة في هذي الحياة سوى العشق", "author": "محمد  جمال"},
{"text": "كان أول تعريف للبشر في التاريخ كله أن البشر حيوانٌ عاشق", "author": "محمد  جمال"},
{"text": "إن أقصى درجات المعرفة هي الحب", "author": "محمد  جمال"},
{"text": "لكن ينبغى أن تتوقفى عن النظر إلى العالم من خلال رأسك وأن تنظرى من خلال قلبك .. هكذا ستعرفين الله", "author": "إليزابيث جيلبرت"},
{"text": "في بعض أحوال الحب، تكون العواطف المحبة لك في الباطن هي المعادية لك في الظاهر.", "author": "مصطفى صادق الرافعي"},
{"text": "أحتاج إلى امرأة شابة، أيها الطبيب. تلك هي مشكلتي", "author": "تشارلز بوكوفسكي"},
{"text": "إن الحب جميل، كأنه الزهور في ضوء الشفق.", "author": "ستيفين كنيج"},
{"text": "إن الحب يشبه الموت احياناً.", "author": "ستيفين كينج"},
{"text": "الله بحكمته أعطى لكلٍ منا ضعفه ، ومن بين نقاط الضعف التي عندي أنّني غير قادر على رفض طلبٍ لكِ", "author": "خالد حسيني"},
{"text": "الحب هو من سيدلك على الحب الحقيقي وليس وهمه...", "author": "منى الليلي( ام حمدة )"},
{"text": "لن يسعني أن أحب إلا الرجل القادر على حمايتي.", "author": "فيكتور هيوجو"},
{"text": "قلت لها بعيني : إغفري لي، أتركيني ..أنتِ مُقدسة لدرجة لا يمكن أن أقترب منكِ", "author": "عبدالرحمن منيف"},
{"text": "لا شيء يعطى على هذه الأرض دون جهد، حتى الحب، أكثر المشاعر طبيعةً وجمالاً.", "author": "محمد الضبع"},
{"text": "فالمشاعر التي تسيطر علي حين أتذكرها تجعلني أقرب إلى المجنون، والأوقات التي يمر فيها طيفها كثيرة لدرجة لا أستطيع أن أفكر بغيرها", "author": "عبدالرحمن منيف"},
{"text": "صباحك سُكّر", "author": "أدهم شرقاوي"},
{"text": "ما هو الحب إن لم يكن التقبّل للطرف الآخر مهما كان؟", "author": "محمد الضبع"},
{"text": "الحب يطغى حتى وأن أذل صاحبه وأبلاه.", "author": "سلطان موسى الموسى"},
{"text": "بإمكان المرء أن يتعرف على الجمال ويقع في حبه خلال ساعة واحدة، ويستمر حبه له بالسرعة ذاتها؛ ولكن حين يأتي الأمر للروح، عليّ أن أتعلّم أولاً كيف أعرفها.", "author": "محمد الضبع"},
{"text": "أقنع نفسي أنه مختلف. لكن الرجال جميعاً لديهم مسحة أنانية، ومسحة قلق أيضاً تمنعهم من البقاء مع امرأة واحدة. هذا ما لا يجب أن أنساه.", "author": "إيفان كليما"},
{"text": "أنا أكثر من يسوع المخلص عذاباً أيها الأب ،أموت كل يوم، أُصلبُ كل ساعة ،أتعذب، إني أتألم ....", "author": "عبدالرحمن منيف"},
{"text": "خرج صوتي مسكيناً وأنا أقول : وقد نعيش الأيام الأخيرة من العمر معاً", "author": "عبدالرحمن منيف"},
{"text": "الملامة لا تميل بالقلب عن الحب، والعذل لا يصرف النفس عن الحقيقة، والإنسان بين قلبه ونفسه كغصن لين في مهب ريح الجنوب وريح الشمال.", "author": "خليل جبران"},
{"text": "الحب يجب أن يكون معلنا وواضحا.. لا استعارات بالحب", "author": "نبال قندس"},
{"text": "الحرية ليست في أن نتخلى عن ما ننال، الحرية أن نتخلى عن من نحب", "author": "إبراهيم الكوني"},
{"text": "والمرأة في عقيدتهم لم تكن يوماً سوى لعنةٍ أخرى في عنق الرجل", "author": "إبراهيم الكوني"},
{"text": "،في صلاة النسك كوني راهبة للعشق", "author": "عدنان فرزات"},
{"text": "هل لهذة الدرجة يجعلنا الحب مغيبين لا نرى أشياء كثيرة مرعبة فى الأشخاص الذين نحبهم", "author": "هالة الملوانى"},
{"text": "حيث كانت حاجتهُ إلى إمرأتهِ شديدة ، لقد كان وجودها حجة يتعلل بها عن إخفاقه", "author": "فيودور دوستويفسكي"},
{"text": "حين يذهب الحب، يذهب الرضا أيضا وكذلك التفاهم.", "author": "Ivan Klíma"},
{"text": "لا شك أن بمقدور الحب أن يجعل من الأشياء الخاوية التافهة ضئيلة القيمة، أشياء ثمينة ذات بهاء ورونق. فالحب لا ينظر بالعين بل بالفؤاد.", "author": "ويليم شكسبير"},
{"text": "كان متأكداً من أن المرأة التي كانت أمامه كانت إمرأة أحلامه، ليته فقط كان يجد الكلمات ليعبر عن ذلك", "author": "يوكيو ميشيما"},
{"text": "منّ مِن الناس أحبُ إليك : إنسانٌ يُميتك حباً، أم إنسانٌ يُحييك كراهة؟", "author": "إبراهيم الكوني"},
{"text": "لا يذهبُ الرجلُ إلى العبودية إلا بخلّين : الأرض والمرأة", "author": "إبراهيم الكوني"},
{"text": "ما زال هذا الأبله يغني عن الحب، كأنه موجود.", "author": "إيفان كليما"},
{"text": "لأن الحب عدوّ الشفقة .. لأن الحب يُحيي ، ولكن الشفقةُ تُميت", "author": "إبراهيم الكوني"},
{"text": "لا ينعدم الترياق لعداوةٍ بين رجل ورجل ، إلا إذا كان في الأمر إمرأة !!", "author": "إبراهيم الكوني"},
{"text": "إن المحبة والجهالة لا تلتقيان", "author": "خليل جبران"},
{"text": "الشخص الغيور يتخيل دائماً أن ثمة من يسرق منه الشخص المحبوب", "author": "جوان ريفيير"},
{"text": "أحياناً تتحول المرأة إلى شيء أكبر من غريزتنا، شيء أشبه بالمُقدس", "author": "عبده خال"},
{"text": "سيارة العرس مزدانة بالورود والأزهار ...", "author": "هدى عبد الرحمن النمر"},
{"text": "لست مشتاقاً إليك ولكنني ألوم جمال رفقتك على رفع سقف توقعاتي بمن أعرف اليوم.", "author": "عبدالله الحسين"},
{"text": "المرء يمكنه بسهولة أن يكون مخدوعاً ويعتبر حباً ما لا يكون في الواقع حباً", "author": "جوان ريفيير"},
{"text": "ليس ثمة وجود لواقع شراستنا ورغبتنا في التملك فحسب، ولكن ثمة أيضاً وجوداً لواقع قمع حاجتنا إلى الحب، واقع لا نعترف به صادقين", "author": "جوان ريفيير"},
{"text": "إن دوافع الحب لدينا محتقرة ومقموعة (..) يترتب على ذلك أن الدارة المفرغة للعدوانية والعنف الإنفجاري تتعزز .. بل من الممكن أن تنهي الحضارة الغربية", "author": "جوان ريفيير"},
{"text": "أريد أن أقول إن الحضارة تبدو في هذه المرحلة مهددة بالتفكك نظراً إلى أن الحب، مع سلطة التوحيد التي يتصف بها، سلبي وتنهكه العدوانية", "author": "جوان ريفيير"},
{"text": "هذه القدرة على التوحّد بشخص آخر عنصر من العناصر الأكثر أهمية في العلاقات الإنسانية بصورة عامة. وهي أيضاً شرط لنحب حباً حقيقياً وقوياً", "author": "ميلاني كلاين"},
{"text": "الحب يقتلنا على حين غرّة، من دون أن يترك آثاراً للجريمة.", "author": "باولو كويلو"},
{"text": "الحب هذا الشيء الخطير الذي يتعذر تفسيره، سيمحو عن وجه الأرض كل ما أنت عليه، ومحله سيحل كل ما يريد حبيبك أن تكونيه.", "author": "باولو كويلو"},
{"text": "لا تغرمي أبداً. الحب سم. ما إن تغرمي حتى تفقدي السيطرة على حياتك. سيصبح قلبك وعقلك ملكاً لشخص آخر.", "author": "باولو كويلو"},
{"text": "الحب هو النور إنه يبنى ويقوى ويعظّم ويحررّ انه سبب الفرح والحياة", "author": "إيزولا بيزانى"},
{"text": "إذا أحببت احداً فـأخبره ليعلم.. وكررها ليطمئن.. واعمل بها ليوقِن..على أن تسكت عنها متى القلب سكت", "author": "راقية جلال الدويك"},
{"text": "نقيض الحب ليس الكره.. نقيض الحب عدم الاكتراث", "author": "يوسف زيدان"},
{"text": "الخطر ليس فى ان نحب الهبه.. ولكن فى ان نحب الهبه أكثر من صاحب الهبة", "author": "إبراهيم الكوني"},
{"text": "الحبُّ أقوى من الشرِّ نفسِه..", "author": "نجيب محفوظ"},
{"text": "عندما نُحِبُ بحق، نعرف أنفسنا ونعرف الآخرين معرفة أفضل", "author": "باولو كويلو"},
{"text": "تخيلي الحياة دونما حب, لابد أنها ستكون مملة إلى حد الموت, على الأقل إن الحب يجعل الوقت يمضي بسرعة ...", "author": "غيوم ميسو"},
{"text": "من هو الإنسان النرجسي ..؟ الإنسان النرجسي هو الذي لم تتح له فرصة أن يحب الآخرين .. وذلك لأن الآخرين لم يقدموا أي حب", "author": "عادل صادق"},
{"text": "هي من تُحوِّل ثاني أكسيد الكراهية إلى أكسجين المحبة", "author": "عائشة سالم الشحي"},
{"text": "من يحظى بكلمة «أحبك» لن يخاف قلبُه أبداً", "author": "عائشة سالم الشحي"},
{"text": "المحب قد يصبر بالمحبوب، ومع المحبوب، وللمحبوب.. لكنه لا يصبر ابداً عن المحبوب.", "author": "يوسف زيدان"},
{"text": "كم هو ممتع الحب الذي يغفر كل شيء", "author": "إيزابيل الليندي"},
{"text": "سأل : هي الحب دين في حد ذاته ؟ فأجبته : هو يعلو على المعتقدات ، و هو الذي يوصل ما تقطعه", "author": "يوسف زيدان"},
{"text": "دلت الشواهد على أن نقيض الحب ليس الكراهية، فكلاهما وجه لعملة واحدة هي العاطفة...نقيض الحب هو عدم الاكتراث", "author": "يوسف زيدان"},
{"text": "لم يأتني للجنس فحسب، بل ليشعر بأنه مرغوب، كما لو أن شغف أمراة أمكنه فعلاً أن يستثير شعوره بأنه قادر على كل شيء", "author": "باولو كويلو"},
{"text": "أن تدع رجلاً يساعد إمرأة، يجلعه دوماً رقيقاً. هذا حلم كل الرجال", "author": "باولو كويلو"},
{"text": "... ولكن الحب لا أدري لماذا لا يجيء في الوقت المناسب.. من أجل هذا أشعر أننا قد نفترق الآن ولكنني واثق أنني لن أحب أحداً كما أحببتك..", "author": "فاروق جويدة"},
{"text": "إذا كان في هذا العالم شيءٌ لا يوجد منه ما يكفي، فهو بلا شكّ الحب", "author": "أندريا هيراتا"},
{"text": "الحبُّ الذي يكتفي بإعطائك جرعة مركّزة من السعادة ليس حبّا ، إنّهُ مجرّد فيلمٍ هزليّ", "author": "غازي عبد الرحمن القصيبي"},
{"text": "من نحبهم حقاً يا هاري، لا يتركونا أبداً.. فهناك أشياء لا يمكن للموت الإقتراب منها، كاللوحة والذكرى والحب.", "author": "جون تيفاني"},
{"text": "حين رأى المعتقدات تعوق الأرواح عن التحليق الحر، قال الشيخ الأكبر: أدينُ بدين الحب..", "author": "يوسف زيدان"},
{"text": "المَحَبَّةُ لَيْسَتْ مُطْلَقاً كَلاماً مَعْسُولاً يُرَدِّدُهُ اللِّسَان،", "author": "مولود بن زادي"},
{"text": "وأنا بدونك لاشيء .. وأنا معك كل شيء", "author": "هشام ريوشامي"},
{"text": "إن تعلقت بإمرأة فسأفضل قتلها على أن أدعها لرجل آخر", "author": "فلاديمير بارتول"},
{"text": "الكتابة مثل الحب، يأتيان في أوانهما دائما.", "author": "ياسر حارب"},
{"text": "ليسً عِندي ما أقولُه، إنّي أكرهُ نفسي، هذا ما يجب أن أُصارِحك به، وعليكِ ألا تقتربي من رجلٍ يكره نفسَه.", "author": "نجيب محفوظ"},
{"text": "كم يحز في نفس امرأة أن تعيش في جوار رجل أحبها في السابق حباً مضطرماً واليوم لا يعبأ بها إلا قليلاً، حتى أنه لا يحاول أن يواري عنها ولعه بأخرى", "author": "فلاديمير بارتول"},
{"text": "لا اود الموت غريقة", "author": "نور البواردى"},
{"text": "الحب غرفة عمليات.. فيه المبضع، وفيه القُطب", "author": "عبادة زياد الحمدان"},
{"text": "العودة للبحث عن الذات يعني الفشل في البحث عن المستقبل", "author": "احمد الفناجيلي"},
{"text": "حين تكون بريئاً ،", "author": "أنسى الحاج"},
{"text": "تسألني لماذا رجعت .. رجعت لأني كنت أحبه .. والحب يسمح لك بقدرة أكثر على المغفرة وأن تنسى الإساءة", "author": "عادل صادق"},
{"text": "في تعريفه للغيرة:", "author": "عادل صادق"},
{"text": "يقول الله لعبده", "author": "مصطفى محمود"},
{"text": "ما الحب في الدنيا إلا حظوظ وجدةد، وقد تأتيك عفوا ما تظن أنه أبعد الأشياء منالا منك.", "author": "مصطفى لطفي المنفلوطي"},
{"text": "قد يردد الشخص عبارات الحب ولكن نبرته تبث معاني الكره، قد يردد عبارات الشجاعة والقوة ونبرته تتمزق خوفا ووجلا.", "author": "إبراهيم عباس"},
{"text": "يبدأ الحب مشواره بالشوق ثم ... (الحنين)، ويعود في نهاية المشوار ومعه (خفي حنين)!!", "author": "محمد السيد محمد"},
{"text": "الحب وحده جعل من ذالك الخليفة متمسكا بثوب إنسانيته.", "author": "مقبول موسى العلوي"},
{"text": "الحياة ايست من صنعنا، لكن الحب كذلك.", "author": "ياسر حارب"},
{"text": "الحب يمزج القلوب والأرواح، يخليها تسابق أحاسيسها، تشوف قبل عيونها، وتسمع قبل آذانها.", "author": "ابراهيم عباس"},
{"text": "رايتها ملاكاً فأخذ ذكرياتي ورحلت", "author": "أحمد الفناجيلي"},
{"text": "-انظر في عينيها لدقيقة واطلب منها أن تنظر إليك وهي تحدثك\"", "author": "حنان لاشين"},
{"text": "قلة من الناس يجيدون الحب فعلاَ", "author": "ريتشارد تمبلر"},
{"text": "نكون أحرارا في كل شيء إلا في مشاعرنا حين نفقد السيطرة عليها", "author": "سلمان العودة"},
{"text": "لا يوجد شئ اسمه الحب يا صديقي، إنه وهم جميل، ينفذ به الرجال إلي عقول النساء ليستولوا علي أجسادهن، صدقني هذه خلاصة تجربتي", "author": "محمد المنسي قنديل"},
{"text": "ارتكبت غلطة المحب الكبرى وهي امتناع العقل والقلب عن استقبال أي رسائل غير مرغوب بها من الحبيب", "author": "رجاء الصانع"},
{"text": "كما أن الأرض الطيبة ينبت فيها الزرع الطيب، كذلك القلب الطيب ينبت فيه الحب الطيب", "author": "محمود الموسوي"},
{"text": "إن هناك حباً يكفي للجميع", "author": "ريتشارد تمبلر"},
{"text": "أن الحب لن يقع عليك من السماء فجأة ... وإنه لن يُقذف في قلبك وقت نومك، لتستيقظ صباحاً، وتجد نفسك قد أحببت", "author": "محمود الموسوي"},
{"text": "الحب سحاب وثير يحمل صاحبه إلى أهدافه بسلام، وليس هدفاً نلهف خلفه كالسراب", "author": "محمود الموسوي"},
{"text": "ليس الحب هدفاً بذاته، إنما هو وسيلة للوصول إلى أهداف كثيرة", "author": "محمود الموسوي"},
{"text": "الحب تحتاج إلى ممارسته يوماً بعد يوم، وستشعر بثقل الكلمات في بداية المطاف لكنك سرعان ما ستحّس بسهولتها وعذوبتها في فمك", "author": "محمود الموسوي"},
{"text": "بعد أن تمارس التودد لزوجتك فإن إشعاع التودد ينعكس عليك حباً صافياً من شوائب التكلف", "author": "محمود الموسوي"},
{"text": "إن للتعبير اللفظي للحب وقعاً كبيراً في النفس، يصعب محوه على مدى الأزمان", "author": "محمود الموسوي"},
{"text": "من المؤكد انها كانت تحبه أكثر من قدرتها علي التحمل، واكبر من طاقتها علي فراقه.", "author": "محمد المنسي قنديل"},
{"text": "نكتشف اننا لا نسعي لمجرد الحب ولكن ما قد يسبغه علي نفوسنا من سمو، تلك النعمة الجامحة التي قد تكون مستحيلة.", "author": "محمد المنسي قنديل"},
{"text": "أجل أؤمن بقوة الحب .. رأيت ذلك في عديد من حالات الامراض المستعصية، قليل من الحب يطيل العمر ويخفف من حدة الألم.", "author": "محمد المنسي قنديل"},
{"text": "صناديق الوهم منتهي صلاحيه واحلام الضائعه", "author": "احمد الفناجيلي"},
{"text": "أن كثيراً من هؤلاء الأزواج يخفون تحت ابتسامتهم قلوباً دامية ونفوساً مغبون حقها في اختيار شريك حياتها", "author": "رجاء الصانع"},
{"text": "قال صلى الله عليه وسلم: \"لا تؤذيني في عائشة", "author": "سليمان الندوي"},
{"text": "الحب المكتمل لا تلوثه نيران الإنتقام", "author": "محمد الجيزاوي"},
{"text": "انشر الحب لا الحرب", "author": "ستيف جوبز"},
{"text": "زيد بن حارثة حمل دون سواه لقب \"الحب\" حب رسول الله.", "author": "خالد محمد خالد"},
{"text": "لماذا ترجم الفتوى قلوب الحب كالشيطان ؟", "author": "دلال البارود"},
{"text": "فيا نسيم الصبا أنت الرسول له", "author": "زكي مبارك"},
{"text": "‫#‏الحب‬ كالايمان فيه شرك وتوحيد", "author": "زكي مبارك"},
{"text": "آمنت بالله وكفرت بالحب", "author": "زكي مبارك"},
{"text": "وتركتُ البابَ خَلفِي", "author": "علاء عبد العليم على"},
{"text": "أُحبُك حين تُثرثرين ..", "author": "علاء عبد العليم على"},
{"text": "الحب فعل وليس شعورا فقط", "author": "سلمان العودة"},
{"text": "ان شهوة الحس مطلب صغير بجانب شهوة الروح", "author": "زكي مبارك"},
{"text": "اساس القوة الوجدانية ان يكون للرجل قلب", "author": "زكي مبارك"},
{"text": "سامحيني إِن كانت كلِماتي في عشقكِ", "author": "علاء عبد العليم على"},
{"text": "كُل ما فيِ حياتي قابِلا للبدلِ", "author": "علاء عبد العليم على"},
{"text": "الحب شقاء كله، وأشقى المحبين جميعا أولئك الذين يحبون بلا أمل ولا رجاء !", "author": "المنفلوطي"},
{"text": "أردت أن اكتب عنك شعرا ,", "author": "علاء عبد العليم على"},
{"text": "لا تقُل لي أُحِبُكِ في عُجالة , بل تهج أحرفها بقلبك و من ثم قلها ببطىء", "author": "علاء عبد العليم على"},
{"text": "في درج مكتبي مخبىء دفتر صغير", "author": "علاء عبد العليم على"},
{"text": "هل كان ذنبي إذا", "author": "علاء عبد العليم على"},
{"text": "في كل ليلة", "author": "علاء عبد العليم على"},
{"text": "هل لكَ أن تَختصِر تِلك الأعوَام", "author": "علاء عبد العليم على"},
{"text": "قلت لها : سمعت أن البلابل عند شرفتك في الصباح تعزف لحنا ما عزف من قبل", "author": "علاء عبد العليم على"},
{"text": "هؤلاء الموحدون فى الحب يتكلمون باسمى ،على بعد الزمان والمكان ، فأنا وانت اول صوت يناغى ضمير الوجود .", "author": "زكي مبارك"},
{"text": "المبارة بيننا غير متكافأة , فقد كنتى أنتى النادي الذي أشجعه طيلة", "author": "علاء عبد العليم على"},
{"text": "قلت لها : هل إليكِ من سبيل دُون قول أعشقُك", "author": "علاء عبد العليم على"},
{"text": "أولئك رجال آمنوا بالحب ،فعظّموه ومجّدوه واستهانوا من اجله بما يقاسى عبّاد الجمال من مصاعب واهوال.", "author": "زكي مبارك"},
{"text": "ان الشرك فى ‫#‏الحب‬ قد يعين على فهم الالوان المختلفة من طبائع الملاح ، وهذا ما قصد اليه فريق من الشعراء الفرنسيين والألمان", "author": "زكي مبارك"},
{"text": "إن كُنت تشتاقُ لتِلك اللهفة التي كانت في عينيها ,", "author": "علاء عبد العليم على"},
{"text": "كي أسترد عافيتي كان لزاما علي", "author": "علاء عبد العليم على"},
{"text": "ﺍﻟﻌﻤﺮ ﺍﻟﺬﻯ ﻣﺮ ﺑﻠﺎ ﻋﺸﻖ ﻛﺄﻧﻪ ﻟﻢ يكن،", "author": "جلال الدين الرومي"},
{"text": "قالت", "author": "أحلام مستغانمي"},
{"text": "أن الحب يشبه الجوع والظمأ لكنه لا يعرف الشبع أبداً", "author": "بول ايلوار"},
{"text": "- إلى أين ؟", "author": "أيمن العتوم"},
{"text": "وهل في نعم الدنيا كلها نعمة أجلّ وأبقى، من أن يكرمك الله بمعرفة ذاته، ومن أن يذيقك لذة القرب منه والتحبب إليه ؟", "author": "محمد سعيد رمضان البوطي"},
{"text": "لو كان الحُب له قلب ،", "author": "أحمد صبري غباشي"},
{"text": ".كلمة واحدة تحررنا من كل أعباء الحياة آلامها،هذه الكلمة هي : الحب.", "author": "سوفوكليس"},
{"text": "قال مُعاذ بن سَهْل: الحبُّ أصعبُ ما ركب، وأسْكر، ما شُرب، وأَفْظع ما لُقِي، وأحْلَى ما اشتُهي، وأَوْجع ما بَطَن، وأشهى ما عَلَن", "author": "أحمد بن عبد ربه الأندلسي"},
{"text": "وَمَا فِي الأَرْضِ أَشْقَى مِنْ مُحِبٍّ ..وَإِنْ وَجَدَ الهَوَى حُلْوَالمَذَاقِ", "author": "زكي مبارك"},
{"text": "ما الحب أصلة مش قرار", "author": "ضياء الرحمن"},
{"text": "طلبت من الله ان يمنح قلبي السعاده", "author": "أحمد الفناجيلي"},
{"text": "الحب الحقيقي هو حب خالد لا حدود له ولا مراء فيه فهو حب عادل نقي يعبر عنه بصورة غير صارخة يمكن أن تراه في كبار السن", "author": "أونوريه دي بلزاك"},
{"text": "يا غاية القصد واقصى المنى", "author": "زكي مبارك"},
{"text": "أنا لست ضد الحب, لكن أتمنى أن يسود العالم الحب القائم على الاحترام المتبادل, لا الحب الذي يرى فيه أحد الطرفين الطرف الآخر أقل منزلة أو أضعف", "author": "وليد خيري"},
{"text": "أحبك يا أنت ، كما أنت", "author": "كريم الشاذلي"},
{"text": "كلما ازداد أَحباؤك, كلما ازداد ضَعفك", "author": "رامي عمار"},
{"text": "لبكاء هذا اليوم صنعت مدامعى", "author": "زكي مبارك"},
{"text": "ومابين دموع التلاقٍ ، ودموع الفراق ، لحظة دونها حياة الابرار فى جناة النعيم", "author": "زكي مبارك"},
{"text": "غمضت عيني", "author": "ياسمين ثابت"},
{"text": "الحمقى وحدهم يبكون رحيل الحب", "author": "غادة السمان"},
{"text": "ابينا ان نطيعكم ابينا...... فلا تلقوا نصيحتكم الينا", "author": "زكي مبارك"},
{"text": "الحب مثل النهر دائما ما يتخذ مجري جديدا اذا ما واجهته أية عوائق", "author": "جريجوري جوديك"},
{"text": "ولأنك لم تهمس في اذني، بل في قلبي، فإنك لم تقبل شفتي. ولكن قبلت روحي‏", "author": "جريجوري جوديك"},
{"text": "فالحُبّ يحتاج إلى لحظة وإنسان ليُحوّل", "author": "أنسي الحاج"},
{"text": "التلسكوب العظيم في استكشاف معاني الحب قد يكون دمعة!", "author": "مصطفى صادق الرافعي"},
{"text": "الحب يخرج من نفسك شخصا غيرك، والبغض يخرج من هذا الشخص غيره، فتحب بنفسين، وتبغض بثلاث", "author": "مصطفى صادق الرافعي"},
{"text": "فإن الحب هو الطرف الشاذ الذي لم يُعرف له وسط ، فإن لم يكن ذاهباً إلى الزيادة مضطردا بها، كان غير شك منحدراً إلى النقص مستمراً فيه", "author": "مصطفى صادق الرافعي"},
{"text": "الحب هو أعظم مجمل للحياة", "author": "لويزا ماي ألكوت"},
{"text": "لكي تنفذ طاقة الحب الحقيقية إلى روحك، ينبغي لروحك أن تكون كما لو أنها وُلدت للتو.", "author": "باولو كويلو"},
{"text": "إزاى يعنى بحبك أقولها", "author": "ضياء الرحمن"},
{"text": "حتى الحب ..", "author": "ضياء الرحمن"},
{"text": "متى ما أدركَ عقلنا المحدود بأن أرواحنا لا تفنى,حينها فقط يصبحُ الحب اغلى مانملك.", "author": "عبدالله العتيبي"},
{"text": "وأنتَ زينة السماء، ولكنّ السماء منك كمرآة سحرية اطلعت فيها حورية من حور الجنّة فأمسكت خيالَ وجهها في لجّة من النور، فأنت خيال وجهها.", "author": "مصطفى صادق الرافعي"},
{"text": "العصافير تعرف الحبّ وتعبّر عنه، وهي بهذه البراءة أصدق من الرجال.", "author": "ليلى العثمان"},
{"text": "الشرف هو الحب\" , هكذا كنت أسمع أمي تقول", "author": "غابرييل غارثيا ماركيث"},
{"text": ".. حقيقة الحب في السينما .. ؟!", "author": "مصطفى محمود"},
{"text": "في غيابكِ افقدُ انفاسي بين الثانية والثانية ألاف المرات,,اهكذا يفعلُ الحب ياعزيزتي؟", "author": "عبدالله العتيبي"},
{"text": "- كلاهما ضارّ بالصّحة . . لكن ضرر السيجارة أرحم من أضرار الحبّ", "author": "ليلى العثمان"},
{"text": ":- الحبّ كالرّيح لا ندري نواياها ولا ماذا تحمل إلينا، والمطلوب أن نواجهها، ننحني ولا ننكسر.", "author": "ليلى العثمان"},
{"text": "لا يمكنك أن تحبي أي شخص حقاً، حتى يسكنك شعور عميق بأن الموت سيباغتك، ويسرقه منك.", "author": "أحلام مستغانمي"},
{"text": "مشكلة الحُب الكبير هي أن أصحابه يبدأون بشكل جميل وينتهون في الفجيعة", "author": "واسيني الأعرج"},
{"text": "ليكن جسدي / لغتي / كبدي / نَفْسي / نَفَسي /صخرةً يُنقشُ عليها تاريخ سقوط الطاغية", "author": "زاهي وهبي"},
{"text": "الحب الحقيقي هو ذلك اليقين بان أرواحنا لا تفنى،،لذلك هو أبدي.", "author": "عبدالله العتيبي"},
{"text": "ربما.. لأنه لم يحدث قبلها أن مارست الحب رسماً .. مع الوطن!", "author": "أحلام مستغانمي"},
{"text": "قصة فرعية، كتبت مسبقاً وحولت مسار حياتي بعد عمر بأكمله، بحكم شيء قد يكون اسمه القدر، وقد يكون العشق الجنوني..", "author": "أحلام مستغانمي"},
{"text": "من رماد جرحٍ تشتعلُ جمرةمن زقزقة طير يخضرُّ الفضاعلى عود يابس تتفتح قبلاتمن سهام كيوبيد تتدفق ينابيع", "author": "زاهي وهبي"},
{"text": " أدركت حين كبرت أن المشكلة ليست في الإنطلاق بسرعة بل في التوقف بسرعة ", "author": "هشام ريوشامي"},
{"text": "الحب حيوان مفترس يريد كل شيء و لا يرضى عن أي شيء.", "author": "أنيس منصور"},
{"text": "إن العين التي تعشق ،", "author": "رانيا شكر"},
{"text": "كلُّ قلبٍ يُنْشِدُ أغنيةً ناقصة، إلى أن يَهْمِسَ قلبٌ آخر بالرّد.", "author": "أفلاطون"},
{"text": "(انني لم اجد امرأة تصلح أما ً لأبنائي الا المرأة التي أحبها)", "author": "فريدريك نيتشه"},
{"text": "إذًا أي شي نتوقعه من العقل فيما يخص الإنسان وميوله؟", "author": "Ernesto Sabato"},
{"text": "كان فعلها منزها عن العلل والأغراض و الحاجة .. كان فعلها مثل زهرة تمنح عطرها لأنها لا تستطيع أن تفعل شيئاً غير أن تمنح عطرها .", "author": "أحمد بهجت"},
{"text": "ويكون الوقت صعباً، وتجيء", "author": "مريد البرغوثي"},
{"text": "من من البشر لم يصبه مرض أسمه الحب", "author": "احمد يوسف"},
{"text": "ماكنش ممكن تخليني", "author": "ياسمين ثابت"},
{"text": "عُد...", "author": "ليديا عادل"},
{"text": "تظاهرا بجهل ما يعرفانه كلاهما ، و ما يعرف كل منهما أن الآخر يعرفه", "author": "غابرييل غارثيا ماركيث"},
{"text": "في الحب..ما انبنى على العاطفة وحدها هدمته العاطفة وحدها. أما الحب العقلاني فإن أول تفكير عميق فيه يهشّمه", "author": "عبد الله حبيب"},
{"text": "أننا حين نكابر في الحب فإننا نغامر به، ونضعه أمام العديد من علامات الاستفهام التي لا تصب إجاباتها في صالح أي من القلوب.", "author": "حمد الحمادي"},
{"text": "فى الحب تُدرك مدى قوة ضعفك", "author": "Muhammad Dakrory"},
{"text": "تجار الحب ..", "author": "زهرا محمد موسى"},
{"text": "الحب له طرق مختلفة واساليب متنوعه تختلف باختلاف الاطراف المتحابه .. يبدأ الحب بمعرفة من نحب وعندما تتوفر المعرفة يتوفر الحب", "author": "مصطفي إمام"},
{"text": "الحب يغير على القلوب ليغيرها", "author": "حمد الحمادي"},
{"text": "ليس عيبا أن نكيل بمكيالين.. العيب أن لا نكيل الحب بما هو أكبر منه.. ولا نكيل الكره بما هو أنبل منه.", "author": "حمد الحمادي"},
{"text": "يجب أن نقنع أنفسنا بأننا نحب من أجل أنفسنا لا من أجل غيرنا، فنحن من يستفيد من الحب، ونحن من يغيرهم الحل.", "author": "حمد الحمادي"},
{"text": "واستخسرت علي الحب وأن أعشق.. وعذرت قلبك فالحب عليه قد شق.", "author": "حمد الحمادي"},
{"text": "الكابرة تجعل من قلوبنا التي كبرت بالحب أصغر، فتضيق على الحب الذي ينسحب منها لأنها لم تعد تتسع له.", "author": "حمد الحمادي"},
{"text": "الحب يكبر القلوب لكن القلوب لا تستطيع تحجيمة، والحب يفتح القلوب دون أن تستطيع هي إغلاقه، والحب يجمع القلوب دون أن تستطيع هي تشتيته.", "author": "حمد الحمادي"},
{"text": "المحبة الحقيقية لا تُطلب،لا تُمنح، ولا تُهدى", "author": "أحمد سلامة"},
{"text": "أحبك بكلمة أخرى..كلمة أعمق..أصدق..أوسع أكبر و أشمل..كلمة لم تستهلكها الألسن..أحبك بكلمة لم ينطق بها المنافق و الكذاب..كلمة لم يتفوه بها عاشق من قبل", "author": "سعود السنعوسي"},
{"text": "يحتفل العشاق بحبهم يوما في السنة..بينما احتفل أنا بأصدقائي طوال العام.", "author": "عبدالله العتيبي"},
{"text": "في الحب ينتهي كل شيء عند اول خطأ..بينما الصداقة لا قيمة لها الا باخطائها.", "author": "عبدالله العتيبي"},
{"text": "ليست جريمة أن نكره. الجريمة أن نكره بلا سبب. وليست فضيلة أن نحب. الفضيلة أن نحب الآخرين من أجلهم لا لأنفسنا", "author": "محمد الهاشمي"},
{"text": "ليس الحب ماتفعله حين نكون معا ياحبيبي", "author": "ياسمين ثابت"},
{"text": "قديما قالوا الاختلاف فى الرأى لا يفسد للود قضية ، ومع ذلك لو عاشوا معنا فى هذه الايام لعلموا ان فى بلادنا أن الاختلاف فى الراى قد يفسد للود قضية", "author": "احمد يوسف"},
{"text": "لقاءٌ ملتبس. لا نعرف فيه مَن مِنّا يعطي ومَن مِنا يأخذ، كنت تقول ذلك للمرأة.. الحُب هو ارتباك الأدوار بين الآخذ والمُعطي. هذا حديثٌ عن الحب..", "author": "مريد البرغوثي"},
{"text": "لیتنا أنا وأنت جئنا العالم قبل اختراع التلفزيون والسینما لنعرف ھل ھذا حب حقًا أم أننا نتقمص ما", "author": "أحمد خالد توفيق"},
{"text": "الحب ليس أعمي فقط بل يقود مسرعاً بجنون", "author": "ياسر أحمد"},
{"text": "و أخر ما بينا وداع ،و كان اولنا حلم", "author": "عمرو وائل"},
{"text": "الحب بين الانسان و الكلب حب بريء , حب دون صراع و دون مشاهد ممزقة و دون تطور .", "author": "ميلان كونديرا"},
{"text": "علاقات الحب مثل الإمبراطوريات ما إن يختفي المبدأ الذي ينبت على أساسه حتى تختفي معه أيضا", "author": "ميلان كونديرا"},
{"text": "عودتني الايام على فراقك .. وعلمتني الليالي نسيانك", "author": "احمد يوسف"},
{"text": " و على قد ما نفسى أعيش وياكى، نادم أصلاً إنى فى يوم بعيش", "author": "عمرو وائل"},
{"text": "‏\" لا أجد نقطة لقاء بينى وبين القمر ,سواءَ أن أعشق", "author": "عمرو وائل"},
{"text": "لا الحب الأول ولا الثاني ولا حتي الثالث .. فكلها امراض تصيب الانسان وعليه أن يتخلص منها فورا !", "author": "احمد يوسف"},
{"text": " بقيتى معىِ,و معَك قلبى,فلا حَياة بدُونه, و لا حياة بدُونِك ", "author": "عمرو وائل"},
{"text": "ولا ولوج إلى ساحة السعادة - في اعتقادي - إلا من أحد أبواب ثلاثة : الإيمان والفن و الحب ، لاشئ يشع بها مثل هذا الخشوع الذي أراه في المعابد.", "author": "يحيى حقي"},
{"text": "الحب <<لحن>> يعزف على أوتار القلب... وكلما قطع العقل منه وتراً، زاد اللحن طرباً!...", "author": "توفيق الحكيم"},
{"text": "الحب هو <<خبر>> يستقى من القلب... ويسأل فيه العقل فيكذبه... ولكن القلب يؤمن به ويجازف بإعلانه، متحملاً وحده مسئولية النشر!...", "author": "توفيق الحكيم"},
{"text": "ليخسأ الزواج إن كان قيداً قاتلاً ولم يكن صداقة ممتعة..... !", "author": "واسيني الأعرج"},
{"text": "نوجد أم نحيا!!", "author": "أحمد بهجت"},
{"text": "أعجبني اسمي وهي تنطقه من شفتيها. كنت كمن يكتشف اسمه ووجوده لأوّل مرّة.", "author": "علي المقري"},
{"text": "في مجتمعاتنا العربية .. نعد الاعتراف بالخطأ فضيلة", "author": "ندى ناصر ضلع أعوج"},
{"text": "ما بالُ وقاري فيكِ قدْ أصبحَ طيشْ\t واللّهِ لقد هَزّمْتِ مِنْ صَبْرِيَ جَيشْ", "author": "ابن الفارض"},
{"text": " لكني منذ تلك الساعة تبينت أنه ما من إثم يمكن ان يطويه النسيان .. ما دام ضمير صاحبه يذكره .! ", "author": "شتيفان تسفايغ"},
{"text": "افترقنا إذًا", "author": "أحلام مستغانمي"},
{"text": "تعتبر الوحدة أشد أنواع الألم قسوة، ولذلك يسعى الإنسان إلى إحاطة نفسه بأناس يحبهم ويحبونه", "author": "ياسر حارب"},
{"text": "الحب لا يُطفئه إلا حب مثله !", "author": "منى أبوزيد"},
{"text": "أمِنكَ للصّبّ عِندَ الوَصْلِ تذكارُ\t وكَيفَ والحُبُّ إظهارٌ وإضمَارُ", "author": "العباس بن الأحنف"},
{"text": "نظن أنفسنا نقاوم حباً ولكننا باستمرار نثبت خطوة تقودنا نحو نقطة اللارجوع", "author": "واسيني الأعرج"},
{"text": "النساء دائما أوراق قابلة لإعادة الكتابة", "author": "محمد حسن علوان"},
{"text": "كي أُحبك؛ كُن أحمق", "author": "بتول علوي"},
{"text": "الحب السعيد . هل هذ طبيعي ,", "author": "فيسوافا شيمبروسكا"},
{"text": "قد يقضي الحب على الصداقة، وقد تفضي الصداقة إلى الحب. فكن صديقا لحبيبتك، وزوجا لصديقتك، وحبيبا لزوجتك!", "author": "شادى عبد السلام"},
{"text": "إن قوما عبدوا الله رهبة، فتلك عبادة العبيد، وآخرون عبدوه رغبة، فتلك عبادة التجار، وآخرون عبدوه محبة وشكرا، فتلك عبادة الأحرار الأخيار.", "author": "علي زين العابدين السجاد"},
{"text": "لماذا سكنت السماءَ يا حبيبي وقلبي أكثر اتساعاً لك؟", "author": "مصطفى محمود"},
{"text": "النساء دائما يفضلن الزواج على الحب", "author": "محمد شكري"},
{"text": "يا اللّذي تطفي الهوى بالصّبر لا بالله", "author": "مظفر النواب"},
{"text": "والعبودية الكاملة إنما يحظى بها من أشرق يقينُه , وطار إلى ربِّه بجناحٍ من الشَّوق والحب", "author": "محمد الغزالي"},
{"text": "هناك اشخاص قد لا نكون قد رأيناهم الا مرة كلمح البصر , ولكن يظل نورهم يضئ لنا حياتنا يوما وراء يوم", "author": "عبدالرحمن ربيع"},
{"text": "الحب توأم الحماقة، و أنا لست مستعدا لتضييع وقتي في حماقات جديدة،،", "author": "جمال بدومة"},
{"text": "أعرف كم الحب مهزوم , ولكنني بكل قساوة ممكنة لم أتصرف لحظةً كالخاسرين", "author": "عبدالله ثابت"},
{"text": "الحب .. هو كل ما لا تستطيع أن تحتفظ به في جيبك لفترةٍ طويلة !", "author": "أحمد صبري غباشي"},
{"text": "لا يوجد دواء أفضل من دواء الحب", "author": "زكرياء ياسين"},
{"text": "لَمّا رَنا حَدَّثَتني النَفسُ قائِلَةً *** يا وَيحَ جَنبِكَ بِالسَهمِ المُصيبِ رُمي", "author": "أحمد شوقي"},
{"text": "لما بتحب فجأة بتلاقي الريموت كنترول بتاع مودك إتحول فجأة من بين إيديك و راح لإيد اللي تحبه", "author": "سارة الأمين"},
{"text": "أبو فراج:", "author": "بيرم التونسي"},
{"text": "النفس تتكون من عواطف وأفكار ومعان تتماسك بواسطة الحب، وهو حب ثلاثي: حب الانسان لخاقه ويجب أن يكون الأقوي ثم حب النفس وحب الناس.", "author": "عادل صادق"},
{"text": "إن الموت هو أصل الأشياء، تموت حكايا الحب.. ويموت المحبون.. وتموت تبعاً لذلك ثمرات العشق.", "author": "سيف الإسلام بن سعود بن عبد العزيز آل سعود"},
{"text": "الأب هو الذي يعرف كيف يقدم الحب و ليس فقط الأوامر و النواهي", "author": "زكرياء ياسين"},
{"text": "أنَزَاعاً في الحُبّ بَعدَ نُزُوعِ، وَذَهَاباً في الغَيّ بَعْدَ رُجُوعِ", "author": "البحتري"},
{"text": "مآ أصعب أن تشتآق", "author": "نآيف بن محمد آلخآلدي"},
{"text": "الحب جنان ومادام قررت تفتح قلبك وتحب وتعيش انسان جواك يبقى لازم تتجنن ما بقاش ينفع العقل خلاص ..", "author": "دعاء جميل"},
{"text": "أعتقدُ أن الرّحِمَ أعمى !! فلو كان يبصرُ ما ترككِ تخرجين منه !ا", "author": "أحمد عمارة"},
{"text": "قال : الحب يا بني , عاطفة وقائية", "author": "محمود أبوزيد"},
{"text": "لا تبخل على من احبك بفرصة الرد الاخير", "author": "لهم"},
{"text": "يقولون يجب أن نبتعد عن الطمع فى الحب .. ولكنى أرى أن الحب ف حد ذاته طمع، طمع الإستحواذ على قلب وعقل وجسد شخص وحدك دون الأخرين", "author": "شادي كمال قنديل"},
{"text": " يلعن ابا القس! ويلعن اباك يا سعد فلم تقل لى ابدا انى لم اعرف الدنيا ولم ادخل حياة, يلعن اباك يا سعد ", "author": "رضوى عاشور"},
{"text": "الحب ليس ذوبانًا .. الحب \"كوكتيل\" جميل", "author": "أحمد عمارة"},
{"text": "وما الحب إلا شبكة يتصيد بها صاحبها ما يشتهي كشبكة العنكبوت .", "author": "علي الوردي"},
{"text": "له آن الدخول", "author": "خلود المعلا"}
];